package basics;

public class swap_java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b=20;
		System.out.println ( " a and b values before swap " + a + " and " +b);
		int c=a;
		a=b;
		b=c;
		System.out.println ( " a and b values after swap " + a + " and " +b);
	}

}
